curl -X POST \
  'https://api.vmware-solutions.cloud.ibm.com/v1/vcenters/nsxt?verify_only=false&check_price=true' \
  -H 'authorization: Bearer <IAM_token>' \
  -H 'Content-Type:application/json' \
  -d '{
      "name": "my-nsxt",
      "dns_type": "vsi",
      "root_domain": "domain.local",
      "domain_type": "primary",
      "subdomain": "mysubdomain",
      "vcs_type": "vcs_nsx_t",
      "vsphere_version": "6.7",
      "host_prefix": "host",
      "license_keys": {
          "vsphere": {
              "key": ""
          },
          "vcenter": {
              "key": ""
          },
          "nsx": {
              "key": "",
              "license_type": "advanced"
          }
      },
      "management": {
          "cluster_name": "my-management-cluster",
          "customized_hardware": {
              "server": "INTEL_INTEL_XEON_4210_2_20",
              "ram": "RAM_192_GB_DDR4_2133_ECC_REG"
          },
          "template_id": "1",
          "shared_storages": [
              {
                  "quantity": 1,
                  "volume": 1000,
                  "iops": "READHEAVY_TIER",
                  "size": "STORAGE_SPACE_FOR_2_IOPS_PER_GB"
              }
          ],
          "quantity": 3,
          "location": "dal12",
          "network": {
              "private_only": false
          }
      },
      "workload": {
          "cluster_name": "my-workload-cluster",
          "customized_hardware": {
              "server": "INTEL_INTEL_XEON_5218_2_30",
              "ram": "RAM_128_GB_DDR4_2133_ECC_REG"
          },
          "template_id": "1",
          "shared_storages": [
              {
                  "quantity": 1,
                  "volume": 1000,
                  "iops": "READHEAVY_TIER",
                  "size": "STORAGE_SPACE_FOR_2_IOPS_PER_GB"
              }
          ],
          "quantity": 2,
          "location": "dal12",
          "network": {
              "private_only": false
          }
      },
      "edge": {
          "location": "dal12",
          "cluster_name": "my-edge-cluster",
          "quantity": 2,
          "network": {
              "private_only": false
          },
          "customized_hardware": {
              "server": "2U_VMW_BARE_METAL_GATEWAY_10_GBPS",
              "ram": "RAM_64_GB_DDR4_2133_ECC_NON_REG"
          }
      },
      "services": [
          {
              "service_info": {
                  "service_id": "vSRX",
                  "variables": {
                      "nickname": "myedge",
                      "vsrx_license_type": "Standard Edition",
                      "target_cluster": "edge"
                  }
              }
          }
      ],
  }

